<script>	function konfirmasi()	{		var r=confirm("Apakah anda yakin ingin menghapus ? pesanan yang sudah dihapus, tidak dapat dikembalikan lagi!")		if (r==true)		{			document.getElementById("form_hapus").submit();		}	}</script><?php $base = '/index.php/administrator/pemesanan/'; 	$id_sel = '';	$u_sel = '';	switch($pilihan) {		case 'id': $id_sel = ' selected="selected"'; break;		case 'username': $u_sel = ' selected="selected"'; break;	}?><?php echo form_open(base_url($base)); ?><div class="grid_12">	<select name="pilihan_cari">		<option value="id"<?php echo $id_sel; ?>>			ID		</option>		<option value="username"<?php echo $u_sel; ?>>			Username		</option>	</select> 	<input type="text" name="input_cari" value="<?php echo $input_cari; ?>"/>	<input type="submit" name="do_cari" value="Cari" class="button"></div><div class="clear"></div></form><form id="form_hapus" action="<?php echo current_url(); ?>" method="POST"><div class="grid_12">	<table class="lists">	<tbody>		<!-- Results table headers -->		<tr>			<th>Id</th>			<th>Username</th>			<th>Tanggal</th>			<th>Total Harga</th>			<th>Produk</th>			<th></th>			<th>Hapus</th>		</tr>		<?php if($rows !== NULL): ?>		<?php foreach($rows as $row): ?>		<tr>			<td><?php echo $row['id']; ?></td>			<td><?php echo $row['pemesan']; ?></td>			<td><?php echo date('H:i:s, d M Y', strtotime($row['tanggal'])); ?></td>			<td>Rp. <?php echo $row['harga_total']; ?></td>			<td><?php echo $row['banyak_produk']; ?></td>			<td><a href="<?php echo base_url($base . 'lihat/' . $row['id']); ?>">Lihat</a></td>			<td><input type="checkbox" name="id_hapus[]" value="<?php echo $row['id']; ?>"></td>		</tr>		<?php endforeach; ?>		<?php endif; ?>	</tbody>	</table></div><div class="clear"></div><input type="hidden" name="do_hapus"><div class="grid_12 list_button">	<div>	<?php echo $pagelink; ?>	</div></div><div class="clear"></div><div class="grid_10">	<a href="#" onClick="konfirmasi();" class="right button">Hapus</a>	<!--input type="submit" name="do_hapus" value="Hapus" class="button right"--></div><div class="grid_2">	<a href="<?php echo base_url($base . 'hapus_semua'); ?>" class="button right">Hapus Semua</a></div></form>