<script>	function konfirmasi()	{		var r=confirm("Apakah anda yakin ingin menghapus ? produk yang sudah dihapus, tidak dapat dikembalikan lagi!")		if (r==true)		{			document.getElementById("form_hapus").submit();		}	}</script><?php	$base = '/index.php/administrator/produk/';	$id_sel = '';	$n_sel = '';	switch($pilihan) {		case 'id': $id_sel = ' selected="selected"'; break;		case 'nama': $n_sel = ' selected="selected"'; break;	}?><?php echo form_open(base_url($base)); ?><div class="grid_12">	<select name="pilihan_cari">		<option value="id"<?php echo $id_sel; ?>>			ID		</option>		<option value="nama"<?php echo $n_sel; ?>>			Nama		</option>	</select> 	<input type="text" name="input_cari" value="<?php echo $input_cari; ?>"/>	<input type="submit" name="do_cari" value="Cari" class="button"></div><div class="clear"></div></form><form id="form_hapus" action="<?php echo current_url(); ?>" method="POST"><div class="grid_12">	<table class="lists">	<tbody>		<!-- Results table headers -->		<tr>			<th>Id</th>			<th>Nama</th>			<th>Kategori</th>			<th>Harga</th>			<th>Dipesan</th>			<th>Dilihat</th>			<th></th>		</tr>		<?php if($rows !== NULL): ?>		<?php foreach($rows as $row): ?>		<?php			if($row['kategori'] === NULL)			{				$row['kategori'] = 'Tanpa Kategori';			}		?>		<tr>			<td><?php echo $row['id']; ?></td>			<td><a href="<?php echo base_url($base . 'edit/' . $row['id']); ?>"><?php echo $row['nama']; ?></a></td>			<td><?php echo $row['kategori']; ?></td>			<td>Rp. <?php echo $row['harga']; ?></td>			<td><?php echo $row['dipesan']; ?></td>			<td><?php echo $row['dilihat']; ?></td>			<td><input type="checkbox" name="id_hapus[]" value="<?php echo $row['id']; ?>"></td>		</tr>		<?php endforeach; ?>		<?php endif; ?>	</tbody>	</table></div><div class="clear"></div><input type="hidden" name="do_hapus"><div class="grid_12 list_button">	<div>	<?php echo $pagelink; ?>	</div></div><div class="clear"></div><div class="grid_8">	<a href="<?php echo base_url($base . 'baru'); ?>" class="left button">Baru</a></div><div class="grid_2">	<a href="#" onClick="konfirmasi();" class="right button">Hapus</a>	<!--input type="submit" name="do_hapus" value="Hapus" class="button right"--></div><div class="grid_2">	<a href="<?php echo base_url($base . 'hapus_semua'); ?>" class="button right">Hapus Semua</a></div></form>