<?php 	$base = '/index.php/pemesanan/'; ?><div class="grid_12">	<table class="lists">	<tbody>		<!-- Results table headers -->		<tr>			<th>Id</th>			<th>Tanggal</th>			<th>Total Harga</th>			<th>Produk</th>			<th>Status</th>			<th></th>		</tr>		<?php if($rows !== NULL): ?>		<?php foreach($rows as $row): ?>		<tr>			<td><?php echo $row['id']; ?></td>			<td><?php echo date('H:i:s, d M Y', strtotime($row['tanggal'])); ?></td>			<td>Rp. <?php echo $row['harga_total']; ?></td>			<td><?php echo $row['banyak_produk']; ?></td>			<td>			<?php				$status_txt = '';				switch($row['status'])				{					case '0': $status_txt = '<span class="merah">Belum bayar</span>'; break;					case '1': $status_txt = '<span class="biru">Lunas</span>'; break;					case '2': $status_txt = '<span class="hijau">Sudah Kirim</span>'; break;				}				echo $status_txt;			?>			<td><a href="<?php echo base_url($base . 'lihat/' . $row['id']); ?>">Lihat</a></td>		</tr>		<?php endforeach; ?>		<?php endif; ?>	</tbody>	</table></div><div class="clear"></div><input type="hidden" name="do_hapus"><div class="grid_12 list_button">	<div>	<?php echo $pagelink; ?>	</div></div>